import {React, useState} from "react";
import { useSelector } from "react-redux";
import CustomerItem from "./CustomerItem";

const CustomerList = (props) => {

    const customersList = useSelector(state=>state.customers.data)

    const [search, setSearch] = useState('')
    const [sort, setSort] = useState('')
    const [data, setData] = useState([])

    const filteredCustomers = () =>  customersList.filter(ele=>{
        return ele.name.toLowerCase().includes(search.toLowerCase()) && 
        ele.mobile.toLowerCase().includes(search.toLowerCase()) &&
        ele.email.toLowerCase().includes(search.toLowerCase())
    })

    const handleSearchChange = (e) => {
        setSearch(e.target.value)
    }

    return (
        <div>
            <h3>List of Customers</h3>
            <input type="search" placeholder="Search by phone or mobile" value={search} onChange={handleSearchChange}/>
            {search?
            <div>
                {filteredCustomers.length>0 ? filteredCustomers.map(customer=>{
                return <CustomerItem key={customer._id} customer={customer}/>
            }): <h3>No Customers Added</h3>}
            </div>:
            <div>
            {customersList.length>0 ? customersList.map(customer=>{
                return <CustomerItem key={customer._id} customer={customer}/>
            }): <h3>No Customers Added</h3>}
            </div>
            }
        </div>
    )
}

export default CustomerList
