        <div>
            <h3>Create a new account</h3>
            <form onSubmit={handleSubmit}>
                <label>Username</label> <br/>
                <input
                type="text"
                name="username"
                value={username}
                onChange={handleChange}
                /> <br/>
                <label>Email Address</label> <br/>
                <input
                type="email"
                name="email"
                value={email}
                onChange={handleChange}
                /> <br/>
                <label>Password</label> <br/>
                <input
                type="password"
                name="password"
                value={password}
                onChange={handleChange}
                /> <br/>
                <label>Business Name (Optional)</label> <br/>
                <input
                type="text"
                name="businessName"
                value={businessName}
                onChange={handleChange}
                /> <br/>
                <label>Address (Optional)</label> <br/>
                <input
                type="text"
                name="address"
                value={address}
                onChange={handleChange}
                /> <br/>
                <input
                type="submit"
                value="Login"
                />
            </form>
        </div>
    )
}

export default Register
