import {React, useState, useEffect} from "react";
import { useDispatch, useSelector } from "react-redux";
import { asyncGetCustomers } from "../../actions/customerActions";
import { asyncGetProducts } from "../../actions/ProductActions";
import BillModal from './BillModal'
import '../../CSS/billModal.css'

const AddBill = () => {

    const dispatch = useDispatch()

    useEffect(()=>{
        dispatch(asyncGetCustomers())
    },[dispatch])
    useEffect(()=>{
        dispatch(asyncGetProducts())
    },[dispatch])

    const customerData = useSelector(state=>state.customers.data)
    const productData = useSelector(state=>state.products.data)

    const [form, setForm] = useState({customerEmail:'', productName:''})
    const [modal, setModal] = useState(false)
    const [filteredCustomerData, setFilteredCustomerData] = useState([])
    const [filteredProductData, setFilteredProductData] = useState([])
    const [customerInfo, setCustomerInfo] = useState({})
    const [cartItems, setCartItems] = useState([])

    const handleChange = (e) =>{
        if (e.target.name==="customerEmail"){
            setForm({...form, customerEmail:e.target.value})
            const result = customerData.filter(ele=>ele.email.toLowerCase().includes(e.target.value))
            if (e.target.value === ''){
                setFilteredCustomerData([])
            } else {
                setFilteredCustomerData(result)
            }
        } else if (e.target.name==="productName"){
            setForm({...form, productName:e.target.value})
            const result = productData.filter(ele=>ele.name.toLowerCase().includes(e.target.value))
            if (e.target.value === ''){
                setFilteredProductData([])
            } else {
                setFilteredProductData(result)
            }
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            email: form.customerEmail,
            product: form.productName
        }
        const formReset = () => {
            setForm({...form, customerEmail:'', productName:''})
        }

    }

    const handleAddBill = () => {
        setModal(!modal)
    }

    const hideModal = () => {
        setModal(!modal)
    }

    const handleCustomerEmail = (id) => {
        const result = filteredCustomerData.find(ele=>ele._id===id)
        setCustomerInfo(result)
        setForm({...form, customerEmail:''})
        setFilteredCustomerData([])
    }

    const handleProductName = (id) => {
        const result = filteredProductData.find(ele=>ele._id===id)
        result.quantity = 1
        setCartItems([...cartItems, result])
        console.log(result)
        setForm({...form, productName:''})
        setFilteredProductData([])
    }

    const finalBill = () => {

    }

    const decQuantity = (id) => {
        const result = cartItems.map((ele) => {
            if (ele._id == id) {
                return { ...ele, ...{ quantity: ele.quantity - 1 } }
            }
            else {
                return { ...ele }
            }
        })
        setCartItems(result)
    }

    const incQuantity = (id) => {
        const result = cartItems.map((ele) => {
            if (ele._id == id) {
                return { ...ele, ...{ quantity: ele.quantity + 1 } }
            }
            else {
                return { ...ele }
            }
        })
        setCartItems(result)
    }

    const removeItem = (id) => {
        const result = cartItems.filter((ele) => {
            return ele._id != id
        })
        const response = window.confirm('Remove Item?')
        response && setCartItems(result)
    }

    const totalBill = () => {
        let total = 0
        for (let i = 0; i < cartItem.length; i++) {
            total += (cartItem[i].quantity * cartItem[i].price)
        }
        return total
    }

    return (
        <div>
            <button onClick={handleAddBill}>Add Bill</button>
            <p></p>
            <BillModal modal={modal} hideModal={hideModal}>
            <form onSubmit={handleSubmit} >
                <label>Customer Email</label> <br/>
                <input
                type="text"
                name="customerEmail"
                value={form.customerEmail}
                onChange={handleChange}
                /> <br/>
                { filteredCustomerData.length > 0 &&
                <div>{filteredCustomerData.map((customer,i)=>{
                    return (
                        <div>
                        <p key={i} onClick={()=>{handleCustomerEmail(customer._id)}}>{customer.email}</p>
                        </div>
                    )
                })}
                </div>}
                <p></p>
                <label>Product Name</label> <br/>
                <input
                type="text"
                name="productName"
                value={form.productName}
                onChange={handleChange}
                /> 
                { filteredProductData.length > 0 &&
                <div>{filteredProductData.map((product,i)=>{
                    return (
                        <div key={i}>
                        <p onClick={()=>{handleProductName(product._id)}}>{product.name}</p>
                        </div>
                    )
                })}</div>
                }
                <p></p>
                {<div>
                    <h4>{customerInfo.name}</h4>
                    <p>{customerInfo.email}</p>
                </div>
                }
                <div>
                    <h4 className="mb-0 pb-2">Items to be billed</h4>

                    <table className='table border-top border-2 border-dark align-middle'>
                        <thead>
                            <tr>
                                <th>Sl. No.</th>
                                <th>Name</th>
                                <th>Price</th>
                                <th>Quantity</th>
                                <th>Total</th>
                                <th>Actions</th>
                            </tr>

                        </thead>
                        <tbody>
                            {cartItems.map((item, i) => {
                                return (
                                    <tr key={item._id}>
                                        <td>
                                            {i + 1}
                                        </td>
                                        <td>
                                            {item.name}
                                        </td>
                                        <td>
                                            {item.price}
                                        </td>
                                        <td><button className="btn btn-secondary me-md-2" onClick={() => decQuantity(item._id)} disabled={item.quantity == 1}> - </button>{item.quantity}<button style={{marginLeft:'8px'}} className="btn btn-secondary" onClick={() => incQuantity(item._id)}> + </button></td>
                                        <td>{item.price * item.quantity}</td>
                                        <td>
                                            <button className="btn btn-danger" onClick={() => { removeItem(item.id) }} >Remove</button>
                                        </td>
                                    </tr>
                                )
                            })}
                        </tbody>
                    </table>

                </div>
                <h2>Total - ₹{finalBill()}</h2>
                <input
                type="submit"
                value="SUBMIT"
                />
                <p></p>
            </form>
            </BillModal>
        </div>
    )
}

export default AddBill
