const initialState = {user:{}, customers:{}, products:{}, bills:{}, isLoggedIn:false}
console.log()

const masterReducer = (state=initialState, action) => {

    switch (action.type) {
        case 'IS_LOGGED_IN': {
            return {...state, isLoggedIn:!state.isLoggedIn}
        }
        // case 'REGISTER': {
        //     return state
        // }
        // case 'LOGIN': {
        //     return {...state, ...{user:action.payload}}
        // }
        default: {
            return state
        }
    }
}

export default masterReducer
