const initialState = {user:{}, customers:{}, products:{}, bills:{}, 
isAuthenticated: localStorage.getItem('token') ? true : false}

const masterReducer = (state=initialState, action) => {

    switch (action.type) {
        case 'LOGIN': {
            return {...state, isAuthenticated:!state.isAuthenticated}
        }
        case 'GET_CUSTOMERS': {
            return {...state, customers:action.payload}
        }
        case 'ADD_CUSTOMERS': {
            return {...state, customers:[...state.customers, action.payload.reverse()]}
        }
        case 'DELETE_CUSTOMER': {
            const result = state.customers.filter(ele=>ele._id!==action.payload)
            return {...state, customers:result}
        }
        case 'EDIT_CUSTOMER': {

        }
        default: {
            return state
        }
    }
}

export default masterReducer
