import axios from 'axios'

export const isLoggedIn = () => {
    return {
        type: 'IS_LOGGED_IN'
    }
}

export const asyncRegister = (formData, formReset) => {
    return (dispatch) => {
        axios.post('https://dct-billing-app.herokuapp.com/api/users/register', formData)
        .then((response)=>{
            const result = response.data
            if(result.hasOwnProperty('errors')){
                alert(result.message)
            } else {
                dispatch(register(result))
                alert('Account successfully created')
                formReset()
            }
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }
}

export const register = (formData) => {
    return {
        type: 'REGISTER',
        payload: formData
    }
}

export const asyncLogin = (formData, formReset) => {
    return (dispatch) => {
        axios.post('https://dct-billing-app.herokuapp.com/api/users/login', formData)
        .then((response)=>{
            const token = response.data
            if(result.hasOwnProperty('errors')){
                alert(result.message)
            } else {
                dispatch(login(token))
                alert("Successfully Logged In")
                localStorage.setItem('token', result.token)
                formReset()
            }
        })
        .catch((err)=>{
            console.log(err.message)
        })
    }
}

export const login = (token) => {
    return {
        type: 'LOGIN',
        payload: token
    }
}
