import {React, useState, useEffect} from "react";
import { useSelector } from "react-redux";
import Billtem from "./Billtem";
import sortedData from "../../selectors/sortedData";
import ReactPaginate from 'react-paginate'

const BillList = (props) => {

    const bills = useSelector(state=>state.bills.data)

    const [search, setSearch] = useState('')
    const [sort, setSort] = useState('')
    const [data, setData] = useState([])
    const [pageNumber, setPageNumber] = useState(0)
    const billsPerPage = 10
    const pagesVisited = pageNumber * billsPerPage

    useEffect(() => {
        setData([...bills])
    },[bills])

    const filteredBills = () => {
        const result = data.filter(ele=>{
            return ele.customer.toLowerCase().includes(search.toLowerCase()) ||
            ele.email.toLowerCase().includes(search.toLowerCase()) ||
            (ele.mobile === Number(search))
        })
        return result
    }

    const handleSortChange = (e) => {
        setSort(e.target.value)
        let sortedCustomers = []
        if(e.target.value === 'ascName'){
            sortedCustomers = sortedData(data, 'name')
        } else if(e.target.value === 'descName'){
            sortedCustomers = sortedData(data, 'name').reverse()
        } else {
            sortedCustomers = [...customers]
        }
        setData(sortedCustomers)
    }

    const handleSearchChange = (e) => {
        setSearch(e.target.value)
    }

    const pageCount = filteredCustomers().length > 0 ? Math.ceil(filteredCustomers().length / billsPerPage) : Math.ceil(data.length / billsPerPage)


    const changePage = ({selected}) => {
        setPageNumber(selected)
    }

    return (
        <div>
            <h3>List of Customers</h3>
            <input type="search" placeholder="Search by phone or mobile" value={search} onChange={handleSearchChange}/>
            <p></p>
            <div>
                <select value={sort} onChange={handleSortChange}>
                    <option value="">Sort By: None</option>
                    <option value="ascName">Name A-Z</option>
                    <option value="descName">Name Z-A</option>
                </select>
            </div>
            <p></p>
            {search?
            <div>
                {filteredCustomers().length>0 ? filteredCustomers().slice(pagesVisited, pagesVisited+billsPerPage).map(customer=>{
                return <Billtem key={bill._id} bill={bill}/>
            }): <h3>No Bills Added</h3>}
            </div>:
            <div>
                {data.length>0 ? data.slice(pagesVisited, pagesVisited+billsPerPage).map(customer=>{
                return <Billtem key={bill._id} bill={bill}/>
            }): <h3>No Bills Added</h3>}
            </div>
            }
            <ReactPaginate
            previousLabel={"Previous"}
            pageCount={pageCount}
            nextLabel={"Next"}
            onPageChange={changePage}
            />
        </div>
    )
}

export default BillList
