import {React, useState, useEffect} from "react";
import { useSelector } from "react-redux";
import Billtem from "./Billtem";
import ReactPaginate from 'react-paginate'

const BillList = (props) => {

    const bills = useSelector(state=>state.bills.data)
    const customers = useSelector(state=>state.customers.data)

    const [search, setSearch] = useState('')
    const [data, setData] = useState([])
    const [searchResults, setSearchResults] = useState([])
    const [pageNumber, setPageNumber] = useState(0)
    const billsPerPage = 10
    const pagesVisited = pageNumber * billsPerPage

    useEffect(() => {
        setData([...bills])
    },[bills])

    const handleSearchChange = (e) => {
        setSearch(e.target.value)
        const filteredBills = () => {
            let result = []
               const customerDetails = customers.filter(ele=>ele.email.toLowerCase().includes(search.toLowerCase()))
               console.log('customer details', customerDetails)
               customerDetails.forEach(customer=>{
                   const filteredBill = bills.filter(bill=>bill.customer===customer._id)
                   return [...result, filteredBill]
               })
               return (result)
           }
        setSearchResults(filteredBills())
    }

    const pageCount = searchResults.length > 0 ? Math.ceil(searchResults.length / billsPerPage) : Math.ceil(data.length / billsPerPage)

    const changePage = ({selected}) => {
        setPageNumber(selected)
    }

    return (
        <div>
            <h3>List of Bills</h3>
            <input type="search" placeholder="Search by customer email" value={search} onChange={handleSearchChange}/>
            <p></p>
            <p></p>
            {search?
            <div>
                {searchResults.length> 0 ? searchResults.slice(pagesVisited, pagesVisited+billsPerPage).map(bill=>{
                return <Billtem key={bill._id} bill={bill}/>
            }): <h3>No Bills Added</h3>}
            </div>:
            <div>
                {data.length>0 ? data.slice(pagesVisited, pagesVisited+billsPerPage).map(bill=>{
                return <Billtem key={bill._id} bill={bill}/>
            }): <h3>No Bills Added</h3>}
            </div>
            }
            <ReactPaginate
            previousLabel={"Previous"}
            pageCount={pageCount}
            nextLabel={"Next"}
            onPageChange={changePage}
            />
        </div>
    )
}

export default BillList
