import {React, useState} from "react";
import { useDispatch, useSelector } from "react-redux";
import { asyncAddCustomer } from "../../actions/customerActions";
import BillModal from './BillModal'
import '../../CSS/billModal.css'

const AddBill = () => {

    const dispatch = useDispatch()

    const customerData = useSelector(state=>state.customers.data)

    const [form, setForm] = useState({customerEmail:'', productName:''})
    const [modal, setModal] = useState(false)

    const handleChange = (e) =>{
        if (e.target.name==="customerEmail"){
            setForm({...form, customerEmail:e.target.value})
        } else if (e.target.name==="productName"){
            setForm({...form, productName:e.target.value})
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            email: form.customerEmail,
            product: form.productName
        }
        const formReset = () => {
            setForm({...form, customerEmail:'', productName:''})
        }
        dispatch(asyncAddCustomer(formData, formReset))
    }

    const handleAdd = () => {
        setModal(!modal)
    }

    const hideModal = () => {
        setModal(!modal)
    }

    const handleAddItem = (e) => {
        e.preventDefault()
    }

    return (
        <div>
            <button onClick={handleAdd}>Add Bill</button>
            <p></p>
            <BillModal modal={modal} hideModal={hideModal}>
            <form onSubmit={handleSubmit} >
                <label>Customer Email</label> <br/>
                <input
                type="text"
                name="customerEmail"
                value={form.customerEmail}
                onChange={handleChange}
                /> <br/>
                <div>{customerData.map((customer,i)=>{
                    return (
                        <p key={i}>{customer.email}</p>
                    )
                })}</div>
                <p></p>
                <label>Product Name</label> <br/>
                <input
                type="text"
                name="productName"
                value={form.productName}
                onChange={handleChange}
                /> 
                <button type="button" onClick={handleAddItem}>Add Item</button>
                <p></p>
                <input
                type="submit"
                value="SUBMIT"
                />
                <p></p>
            </form>
            </BillModal>
        </div>
    )
}

export default AddBill
