import { isLoggedIn } from "../actions/userActions"

const initialState = {user:{}, customers:{}, products:{}, bills:{}, isLoggedIn:false}
console.log()

const masterReducer = (state=initialState, action) => {

    switch (action.type) {
        case 'IS_LOGGED_IN': {
            return {...state, ...{isLoggedIn:!isLoggedIn}}
        }
        case 'REGISTER': {
            return state
        }
        case 'LOGIN': {
            return {...state, ...{user:action.payload}, ...{isLoggedIn:!isLoggedIn}}
        }
        default: {
            return state
        }
    }
}

export default masterReducer
