import {React, useState} from "react";
import { useDispatch } from "react-redux";
import { asyncEditProduct } from "../../actions/ProductActions";

const EditProduct = (props) => {

    const dispatch = useDispatch()

    const {product, handleToggle} = props

    const [form, setForm] = useState(
        {productName:customer.name? customer.name: '',
        customerNumber:customer.mobile? customer.mobile: '',
        productPrice:customer.email? customer.email: ''}
        )

    const handleChange = (e) =>{
        if (e.target.name==="productName"){
            setForm({...form, productName:e.target.value})
        } else if (e.target.name==="customerNumber"){
            setForm({...form, customerNumber:e.target.value})
        } else if (e.target.name==="productPrice"){
            setForm({...form, productPrice:e.target.value})
        }
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const formData = {
            name: form.productName,
            mobile: form.customerNumber,
            email: form.productPrice
        }
        const id = customer._id
        dispatch(asyncEditCustomer(formData, id))
        handleToggle()
    }

    const handleCancel = () =>{
        handleToggle()
    }

    return (
        <div>
            <form onSubmit={handleSubmit} >
                <label>Customer Name</label> <br/>
                <input
                type="text"
                name="productName"
                value={form.productName}
                onChange={handleChange}
                /> <br/>
                <p></p>
                <label>Customer Email</label> <br/>
                <input
                type="email"
                name="productPrice"
                value={form.productPrice}
                onChange={handleChange}
                /> <br/>
                <p></p>
                <input
                type="submit"
                value="Save"
                /> 
                <button type='button' onClick={handleCancel}>Cancel</button>
                <p></p>
            </form>
        </div>
    )
}

export default EditProduct
